insert into MasterIndikator(id,id_aspek,nama,deskripsi,id_pembilang,id_penyebut,default_bobot)values(76,5,'Terlaksananya toilet bersih lebih dari 80% ','Toilet bersih lebih dari 80% berdasarkan kriteria ketersediaan sarana prasarana, bagaimana kondisinya serta upaya pemeliharaan yang dilakukan serta inovasi kenyamanan toilet yang ada di unit yang memiliki nilai di atas batas standar minimum. Kegiatan ini diukur berdasar kuesioner yang diisi, didokumentasikan oleh unit dan divalidasi oleh tim smart eco-campusJika jumlah toilet bersih yang sesuai standar < 80%, maka capaian kinerja indikator ini adalah 0. Dan jika lebih dari atau sama dengan 80%, capaiannya adalah 1',93,3,7)
